import React, { useState } from 'react';

import Form from 'react-bootstrap/Form';
import './App.css';
import axios from 'axios';



export function Task(){

    const[formData,setFormData] = useState({
        title:'',
        description:'',
        date:'',
        duedate:'',
        status:'',
    });
    
    const [fetchData,setFetchData] = useState([]);

    const updateFormData = (field, value) => {
        setFormData(prevData => ({
          ...prevData,
          [field]: value
        }));
      };
      
    const[formErrors,setFormErrors] = useState({
        title:'',
        description:'',
        date:'',
        duedate:'',
        status:'',
    });
    const validateForm=()=>{
        let valid = true;
        const newFormErrors = {...formErrors};

        if(formData.title.trim() === '')
        {
            newFormErrors.title = "title is required";
            valid = false;
        }
        if(formData.description.trim() === '')
        {
            newFormErrors.description = "description is required";
            valid = false;
        }
        if(!formData.date)
        {
            newFormErrors.date = "date is required";
            valid = false;
        }
        if(!formData.duedate)
        {
            newFormErrors.duedate = "duedate is required";
            valid = false;
        }
        if(formData.status.trim() === '')
        {
            newFormErrors.status = "status is required";
            valid = false;
        }
        setFormErrors(newFormErrors);
        return valid;
    };
    const handleSubmit= async(e) => {

        e.preventDefault();
        if(validateForm())
        {
            const token = localStorage.getItem('token');
            if(token)
            {
                try{
                
                    const result = await axios.post('http://localhost:5000/addtask',
                        {
                            title: formData.title,
                            description: formData.description,
                            date: formData.date,
                            duedate: formData.duedate,
                            status: formData.status
                        },
                        {
                            headers: { Authorization: `Bearer ${token}` }
                        }
                    );
                    console.log(result);
                    
                    fetchUpdatedData();
                }
                catch(err){
                    throw new Error('Error in token authentication');
                }
            }
            else
                console.log('No token found');
        }
    };
    const fetchUpdatedData= async() => {
       
            try{
                const response = await axios.get('http://loaclhost:5000/retrieve');
                setFetchData(response.data);
                setFormData({
                    title:'',
                    description:'',
                    date:'',
                    duedate:'',
                    status:''
                });
            }
            catch{
                console.log('Error fetching updated data');
            }
        };
        useEffect(() => {
            fetchUpdatedData();
          }, []);
    return(
        <div className='App'>
            <h2 style={{ color: "blue" }}>Tasks</h2><br /><br />

            <div className='container'>
                <h5>Title <Form.Control 
                            id="title" type="text" value={formData.title}
                            onChange={(e)=>updateFormData("title",e.target.value)} placeholder='enter the title'/></h5>
                <span style={{color:"red"}}>{formErrors.title}</span>
            </div><br/>

            <div className='container'>
                <h5>Description <Form.Control 
                            id="description" type="text" value={formData.description}
                            onChange={(e)=>updateFormData("description",e.target.value)} placeholder='enter the description'/></h5>
                <span style={{color:"red"}}>{formErrors.description}</span>
            </div><br/>

            <div className='container'>
                <h5>status <Form.Control 
                            id="status" type="text" value={formData.status}
                            onChange={(e)=>updateFormData("status",e.target.value)} placeholder='enter the status'/></h5>
                <span style={{color:"red"}}>{formErrors.status}</span>
            </div><br/>

            <div className='container'>
                <h5>Date <Form.Control 
                            id="date" type="Date" value={formData.date}
                            onChange={(e)=>updateFormData("date",e.target.value)} placeholder='enter the date'/></h5>
                <span style={{color:"red"}}>{formErrors.date}</span>
            </div><br/>

            <div className='container'>
                <h5>Duedate <Form.Control 
                            id="duedate" type="Date" value={formData.duedate}
                            onChange={(e)=>updateFormData("duedate",e.target.value)} placeholder='enter the duedate'/></h5>
                <span style={{color:"red"}}>{formErrors.duedate}</span>
            </div><br/>

            <br/>

            <button className="btn btn-success"
            onClick={handleSubmit}>Add
            </button><br/>

            <div>
                <h3>Updated tasks</h3><br/>

                <table className="table">
                    <tr>
                        <th className="th">Id</th>
                        <th className="th">Title</th>
                        <th className="th">Description</th>
                        <th className="th">Date</th>
                        <th className="th">Duedate</th>
                        <th className="th">Status</th>
                    </tr>

                    <tbody>
                        {fetchData.map((item,index) => (
                            <tr key={index}>
                                <td className="td">{item.id}</td>
                                <td className="td">{item.title}</td>
                                <td className="td">{item.description}</td>
                                <td className="td">{item.data}</td>
                                <td className="td">{item.duedate}</td>
                                <td className="td">{item.status}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}
