import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUserAlt, FaLock } from "react-icons/fa";
import Form from 'react-bootstrap/Form';
import './App.css';
import axiosInstance  from './interceptor';

export function Sign() {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    error: ''
  });
  const navigate = useNavigate();

  const updateFormData = (field, value) => {
    setFormData(prevData => ({
      ...prevData,
      [field]: value
    }));
  };
  
  const [formErrors, setFormErrors] = useState({
    username: '',
    password: '',
    error: ''
  });

  const validateForm = () => {
    let valid = true;
    const newFormErrors = { ...formErrors };

    if (formData.username.trim() === '') {
      newFormErrors.username = 'Username is required';
      valid = false;
    } 

    if (formData.password.trim() === '') {
      newFormErrors.password = 'Password is required';
      valid = false;
    }

    setFormErrors(newFormErrors);
    return valid;
  };
  
  const handleSubmit = async(e) => {
    e.preventDefault();
    if (validateForm()) { 
        try{
            const response = await axiosInstance.post('/auth/login',{
              username: formData.username,
              password: formData.password});
              console.log(response);
            const token = response.data.token;
            if (token) {
              localStorage.setItem('token', token);

              console.log(token);
              navigate('/home');
            } else {
              setFormErrors({ ...formErrors, error: 'Invalid Credentials' });
            }
        }
        catch (err) {
          console.error(err); 
          setFormErrors({ ...formErrors, error: 'An error occurred while logging in' }); 
        }
    }
  };

  return (
    <div className="main">
      <div className='navbar'>
        <div className='icon'>
        <h1 className = "title-container" style={{ color: "blue"}}>Login page</h1><br /><br />
        </div>
      </div>
    </div>
    
  );
}
